#include <iostream>#include <string>class Comps{public:    static bool intAscending(int a, int b)    {        return a > b;    }    static bool intDescending(int a, int b)    {        return a <= b;    }    static bool doubleAscending(double a, double b)    {        return a >= b;    }    static bool doubleDescending(double a, double b)    {        return a <= b;    }    static bool stringAscending(std::string a, std::string b)    {        return a > b;    }    static bool pairAscending(std::pair<int, int> a, std::pair<int, int> b)    {        if (a.first > b.first)            return true;        if (a.first == b.first)        {            if (a.second > b.second)                return true;            else if (a.second < b.second)                return false;            else                return false;        }        if (a.first < b.first)            return false;    }};